import React, { useState } from "react";
import "./App.css"; // üî• Î∞òÎìúÏãú App.css ÌååÏùºÏùÑ import Ìï¥Ï§òÏïº Ìï©ÎãàÎã§.

function CustomButton(props) {
  const { color, onClick, children } = props;
  if (color) {
    return <button onClick={props.onClick}>{props.children}</button>;
  }
}

function User(props) {
  return (
    <div className="box-style">
      {props.user.age}ÏÇ¥ - {props.user.name}
      <CustomButton
        onClick={() => {
          props.handleDelete(props.user.id);
        }}
      >
        ÏÇ≠Ï†ú
      </CustomButton>
    </div>
  );
}
const App = () => {
  const [users, setUsers] = useState([
    { id: 1, age: 30, name: "ÏÜ°Ï§ëÍ∏∞" },
    { id: 2, age: 24, name: "ÏÜ°Í∞ï" },
    { id: 3, age: 21, name: "ÍπÄÏú†Ï†ï" },
    { id: 4, age: 29, name: "Íµ¨ÍµêÌôò" },
  ]);
  const [name, setName] = useState("");
  const [age, setAge] = useState("");
  const addUserHandler = () => {
    const newUser = {
      id: users.length + 1,
      age: age,
      name: name,
    };
    setUsers([...users, newUser]);
  };
  const deleteUserHandler = (id) => {
    const newUserList = users.filter((user) => user.id !== id);
    setUsers(newUserList);
  };
  return (
    <div>
      <div className="app-style">
        {users.map((user) => {
          return (
            <User
              handleDelete={deleteUserHandler}
              user={user}
              key={user.id}
            ></User>
          );
        })}
      </div>
      <input
        value={name}
        placeholder="Ïù¥Î¶ÑÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî"
        // Ïù∏Ìíã Ïù¥Î≤§Ìä∏Î°ú Îì§Ïñ¥Ïò® ÏûÖÎ†• Í∞íÏùÑ nameÏùò Í∞íÏúºÎ°ú ÏóÖÎç∞Ïù¥Ìä∏
        onChange={(e) => setName(e.target.value)}
      />
      <input
        value={age}
        placeholder="ÎÇòÏù¥Î•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî"
        // Ïù∏Ìíã Ïù¥Î≤§Ìä∏Î°ú Îì§Ïñ¥Ïò® ÏûÖÎ†• Í∞íÏùÑ ageÏùò Í∞íÏúºÎ°ú ÏóÖÎç∞Ïù¥Ìä∏
        onChange={(e) => setAge(e.target.value)}
      />
      <CustomButton onClick={addUserHandler}>Ï∂îÍ∞ÄÌïòÍ∏∞</CustomButton>
    </div>
  );
};

export default App;
